<?php


class FeedToPosts_Menu
{

    /**
     * @var FeedToPosts_Menu
     * @access private
     */
    private $FeedToPosts_menuPage;

    /**
     * FeedToPosts_Menu constructor.
     * @param $menuPage
     */
    public function __construct($menuPage)
    {
        $this->FeedToPosts_menuPage = $menuPage;
    }

    /**
     * add menu
     */
    public function FeedToPosts_menuinit()
    {
        add_action('admin_menu', array($this, 'FeedToPosts_addMenuPage' ));
    }

    /**
     * Creates the menu item and calls on the Menu Page object to FeedToPosts_render
     */
    public function FeedToPosts_addMenuPage()
    {
        $FeedToPosts_iconSvg = 'data:image/svg+xml;base64,';
        add_menu_page(
            'Feed to Posts',
            'Feed to Posts',
            'manage_options',
            'FeedToPosts-plugin',
            array( $this->FeedToPosts_menuPage, 'FeedToPosts_render'),
            $FeedToPosts_iconSvg
        );
    }
}
